var sms = require('./index.js')
sms.qcloudConfig = {
  sdkappid: 'xxx',
  appkey: 'xxxx'
}
sms.sendMultiMsg({
  tel: [{
    nationcode: '86',
    mobile: 'xxxx'
  }, {
    nationcode: '86',
    mobile: 'xxxx'
  }],
  type: 0,
  msg: 'xx您的登录验证码，请于xx分钟内填写。如非本人操作，请忽略本短信。'
}).then(result => {
  console.debug('成功')
  console.debug(result.text)
}).catch(err => {
  console.debug('出错了')
  console.debug(err)
})

sms.sendMultiTemplateMsg({
  tel: [{
    nationcode: '86',
    mobile: 'xxxx'
  }, {
    nationcode: '86',
    mobile: 'xxxx'
  }],
  tplId: 'xxxx',
  params: [
    '1234',
    '4'
  ]
}).then(result => {
  console.debug('成功')
  console.debug(result.text)
}).catch(err => {
  console.debug('出错了')
  console.debug(err)
})

sms.sendSingleMsg({
  tel: {
    nationcode: '86',
    mobile: 'xxxx'
  },
  type: 0,
  msg: 'xxxx为您的登录验证码，请于xxxx分钟内填写。如非本人操作，请忽略本短信。'
}).then(result => {
  console.debug('成功')
  console.debug(result.text)
}).catch(err => {
  console.debug('出错了')
  console.debug(err)
})

sms.sendTemplateMsg({
  tel: {
    nationcode: '86',
    mobile: 'xxxx'
  },
  tplId: 'xxxx',
  params: [
    '1235',
    '4'
  ]
}).then(result => {
  console.debug('成功')
  console.debug(result.text)
}).catch(err => {
  console.debug('出错了')
  console.debug(err)
})
